import axios from 'axios';

class ActionProvider {
    constructor(createChatBotMessage, setStateFunc) {
      this.createChatBotMessage = createChatBotMessage;
      this.setState = setStateFunc;
    }
  
    // new method
    greet() {
      const greetingMessage = this.createChatBotMessage("Hi, friend.");
      this.updateChatbotState(greetingMessage);
      console.log(greetingMessage);
    }

    ujjwalFunction()
    {
      const ujjwalmsg=this.createChatBotMessage("asdkjfhkjasdgfkjgDFJS");
      this.updateChatbotState(ujjwalmsg);
    }
  
    handleJavascriptList = () => {
      const message = this.createChatBotMessage(
        "Fantastic, I've got the following resources for you on Javascript:",
        {
          widget: "javascriptLinks",
        }
      );
  
      this.updateChatbotState(message);
    }

    handleSpringbootList = () => {
      const message=this.createChatBotMessage(
        "Fantastic, I've got the following resources for you on Springboot",
        {
          widget:"SpringbootLinks",
        }
      );

      this.updateChatbotState(message);
    };




   fetchApiMessage=(message)=>
   {

    //const response =  axios.get('http://127.0.0.1:5000/chat?message=who is matt asay?');
    
     axios.get('http://127.0.0.1:5000/chat?message='+message).then((res)=>{this.updateChatbotState(res);})
    //const data =  response.json();
    //const response = 
    console.log("********************************");
    //console.log(res);
      

    
   };







    updateChatbotState(message) {
      // NOTICE: This function is set in the constructor, and is passed in from the top level Chatbot component. The setState function here actually manipulates the top level state of the Chatbot, so it's important that we make sure that we preserve the previous state.
  
      this.setState((prevState) => ({
        ...prevState,
        messages: [...prevState.messages, message],
      }));
    }
  }
  
  export default ActionProvider;
